// set initial state  
const initialState = {
   loggedInUser: null
 }

const stateReducer = (state=initialState, action) => {
    switch(action.type) {
        case "setLoggedInUser": {
            return {
                ...state,
                loggedInUser: action.data
            }
        }
        default: 
            return state
    }
}

  // Create state reducer store and dispatcher
  const [store, dispatch] = useReducer(stateReducer, initialState)
