import React, { useState, useEffect } from "react";
import axios from 'axios'
import { useGlobalState } from "../../config/store"
// import {loginUser} from "../../services/loginUser"

const UserLogin = () => {

  // set initial values for local state 
  const initialvalues = {
    // id: 0,
    username: "",
    password: ""
  }

  const [values, setValues] = useState(initialvalues)
  // destructure store and dispatch from global state
  const {store, dispatch} = useGlobalState()
  // destructure loggedInUser from store
  const {loggedInUser} = store

  const handleInputChange = e => {
    const { name, value } = e.target
    setValues({
      ...values,
      [name]: value
    })    
  }

  useEffect(() => {
    dispatch({
      type: "setLoggedInUser",
      data: values
    })
  }, [values])

  const loginUser = async (data) => {
    await axios({
        method: "POST",
        data: {
            username: data.username,
            password: data.password
        },
        withCredentials: true, 
        url: "http://localhost:5000/users/login",
    }).then(res => {
        console.log('response=', res)
        if (res.status === 200) {
            setValues({username: data.username})
            console.log('store=' + store)
        }
    })
  }

  const formSubmit = async (e) => {
    e.preventDefault()
    // console.log(loggedInUser)
    await loginUser(values)

  }


  return (
    <div className="container">
      <div classame="row">
        <div className="col s6">
          <h1>Login</h1>
          <form onSubmit={formSubmit}>
            <label htmlFor="username">
                Username
                <input
                  name="username"
                  type="text"
                  value={values.username}
                  onChange={handleInputChange}
                />
            </label>
            <label htmlFor="password">
                Password
                <input
                name="password"
                type="text"
                value={values.password}
                onChange={handleInputChange}
                />
            </label>
            <input type="submit" value="submit" className="btn waves-effect waves-light" />
          </form>
          <br/>
          <span>Don't have an account? <strong><a href="/register">Register</a></strong></span>
        </div>
      </div>
    </div>
  );
};

export default UserLogin;
