import React, { useState, useEffect } from "react";

const initialvalues = {
  id: 0,
  username: '',
  password: ''
}

const UserLogin = () => {

  const [values, setValues] = useState()

  return (
    <div className="container">
      <div classame="row">
        <div className="col s6">
          <h1>Login</h1>
          <form onSubmit={handleSubmit(onSubmit)}>
            <label htmlFor="username">
                Username
                <input
                name="username"
                type="text"
                ref={register({
                    required: {
                    value: true,
                    message: "You must enter a username",
                    },
                    pattern: {
                    value: /^[a-zA-Z0-9]{4,10}$/,
                    message: "must use at least 4 characters",
                    },
                })}
                />
            </label>
            {errors.username && (
              <p style={{ color: "red" }}>{errors.username.message}</p>
            )}
            <label htmlFor="password">
                Password
                <input
                name="password"
                type="text"
                ref={register({
                    required: {
                    value: true,
                    message: "You must enter a password",
                    },
                    pattern: {
                    value: /^[a-zA-Z0-9]{6,}$/i,
                    message:
                        "Must use at least 6 characters. Must be a combination of uppercase & lowercase letters and numbers",
                    },
                })}
                />
            </label>
            {errors.password && (
              <p style={{ color: "red" }}>{errors.password.message}</p>
            )}
            <input type="submit" className="btn waves-effect waves-light" />
          </form>
          <br/>
          <span>Don't have an account? <strong><a href="/register">Register</a></strong></span>
        </div>
      </div>
    </div>
  );
};

export default UserLogin;
