import { useReducer } from 'react'

// set initial state  
const initialState = {
   loggedInUser: null
 }

const stateReducer = (state=initialState, action) => {
    switch(action.type) {
        case "setLoggedInUser": {
            return {
                ...state,
                loggedInUser: action.data
            }
        }
        default: 
            return state
    }
}
